import React, { useState } from "react";
import "../styles/Sidebar.css";

const Sidebar = ({ onSelectFactor, onSelectAddress }) => {
  const [isOpen, setIsOpen] = useState(true);
  const [selectedFactor, setSelectedFactor] = useState("");
  const [address, setAddress] = useState("");

  const factors = ["AQI", "CO2 Level", "Traffic", "Weather"];

  const handleFactorChange = (event) => {
    const factor = event.target.value;
    setSelectedFactor(factor);
    onSelectFactor(factor);
  };

  const handleAddressSearch = async () => {
    if (!address) return;

    try {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(address)}`
      );
      const data = await response.json();

      if (data.length > 0) {
        const { lat, lon } = data[0];
        onSelectAddress({ lat: parseFloat(lat), lng: parseFloat(lon) });
      } else {
        alert("Address not found. Try again.");
      }
    } catch (error) {
      console.error("Error fetching location:", error);
    }
  };

  return (
    <div className={`sidebar ${isOpen ? "open" : "closed"}`}>
      <button className="toggle-button" onClick={() => setIsOpen(!isOpen)}>
        {isOpen ? "←" : "→"}
      </button>
      {isOpen && (
        <div className="sidebar-content">
          <h3>Select Factor</h3>
          <select value={selectedFactor} onChange={handleFactorChange} className="dropdown">
            <option value="" disabled>Select a factor</option>
            {factors.map((factor) => (
              <option key={factor} value={factor}>
                {factor}
              </option>
            ))}
          </select>

          <h3>Search Address</h3>
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="Enter address..."
            className="address-input"
          />
          <button onClick={handleAddressSearch} className="search-button">
            Search
          </button>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
